import commonController from '@/controllers/commonController';
export default {
  name: 'quiz',
  data: function data() {
    return {
      isConnected: false,
      current_quote: 'Maybe if we tell people the brain is an app, they\'ll start using it.',
      isLastQuestion: false,
      question_number: 1,
      showError: false,
      errorMessage: '',
      timeout: false,
      selectedAnswer: '',
      userInGame: true,
      circleProgress: 0,
      showException: false,
      showingQuestion: false,
      socketMessage: '',
      answerForQuestion: '',
      buttonState: [],
      buttonDisableState: false,
      question: {
        current_question: 'what is the capital of Prince Edward Island ',
        answers: ["Charlottetown", "Georgetown", "Montague"]
      },
      randomQuotes: ['Do not take life too seriously. You will never get out of it alive.', 'Always remember that you are absolutely unique. Just like everyone else.', 'Happiness is having a large, loving, caring, close-knit family in another city.', 'It takes considerable knowledge just to realize the extent of your own ignorance.', 'We are all here on earth to help others; what on earth the others are here for I don\'t know.', 'We are all here on earth to help others; what on earth the others are here for I don\'t know.']
    };
  },
  sockets: {
    connect: function connect() {
      // Fired when the socket connects.
      this.isConnected = true;
      console.log('connected');
      this.joinChannel();
    },
    disconnect: function disconnect() {
      this.isConnected = false;
      console.log('disconnected');
    },
    // Fired when the server sends something on the "msg of questions room".
    msg: function msg(data) {
      console.log('receiving data from questions');
      var number_of_records = data.body[0].number_of_records;
      this.question = data.body[0].question[0];

      if (this.question_number === number_of_records) {
        this.isLastQuestion = true;
      }

      this.question_number += 1;
      this.showException = false;
      this.showError = false;
      this.showingQuestion = true;
      this.timeout = false;
      this.selectedAnswer = '';
      this.buttonState = [];
      this.startTimer(40);
      this.circleProgress = 0;
      this.buttonDisableState = false;
      console.log(this.question);
    }
  },
  mounted: function mounted() {
    console.log('local', localStorage.getItem('participantId'));

    if (localStorage.getItem('participantId') === null || localStorage.getItem('quizId') == null) {
      this.$router.push('/');
    }

    this.showQuotes();
  },
  methods: {
    joinChannel: function joinChannel() {
      // Send the "pingServer" event to the server.
      console.log("joining questions");
      this.$socket.emit('join', 'questions');
    },
    showEliminateMessage: function showEliminateMessage() {
      console.log(this.userInGame && this.showingQuestion);
      return this.userInGame && this.showingQuestion;
    },
    showExceptionOnDOM: function showExceptionOnDOM() {
      return this.showException ? "exception" : "success";
    },
    startTimer: function startTimer(timeToExpire) {
      var _this = this;

      console.log('question', this.question_number);
      console.log('last question', this.isLastQuestion);
      var timer = 0;
      var timerInterval = setInterval(function () {
        _this.circleProgress = parseInt(timer / timeToExpire * 100);
        timer = timer + 1;

        if (_this.circleProgress > 60) {
          _this.showException = true;
        }

        if (_this.circleProgress >= 99) {
          _this.timeout = true;

          if (_this.isLastQuestion === true) {
            setTimeout(function () {
              console.log('showing answer for 6 sec');

              _this.$router.push('results');
            }, 6000);
          }

          clearInterval(timerInterval);
        }
      }, 200);
    },
    showQuotes: function showQuotes() {
      var _this2 = this;

      var quoteNo = 0;
      var showQuotesInterval = setInterval(function () {
        _this2.current_quote = _this2.randomQuotes[quoteNo];
        quoteNo += 1;

        if (quoteNo >= _this2.randomQuotes.length) {
          quoteNo = 0;
        }
      }, 4000);
    },
    displayError: function displayError(message) {
      console.log('displaying error');
      this.errorMessage = message;
      this.showError = true;
    },
    setAnswer: function setAnswer(answer, i) {
      var _this3 = this;

      console.log(answer);

      if (this.timeout === true) {
        this.displayError('You were late. Question timed out.');
        return false;
      }

      if (this.selectedAnswer != '' && answer != this.selectedAnswer) {
        this.displayError('You have already selected the answer');
        return false;
      } else {
        this.buttonState[i] = true;
        this.selectedAnswer = answer;
        this.buttonDisableState = true;
        var obj = {
          'participant_id': localStorage.getItem('participantId'),
          'quiz_id': localStorage.getItem('quizId'),
          'question_id': this.question.id,
          'question_option_id': this.question.options[i].id
        };
        console.log("answer", this.question.options[i]);
        commonController.submitAnswer(obj).then(function (response) {
          console.log('response for submit answer', response);
        }).catch(function (e) {
          console.log('error', e);

          _this3.errors.push(e);
        });
      }
    }
  }
};
