import commonController from "../controllers/commonController";
export default {
  name: 'quiz',
  data: function data() {
    return {
      quizId: '',
      isConnected: false,
      question_number: 1,
      isLastQuestion: false,
      timeout: false,
      timeToStart: 60,
      circleProgress: 0,
      optionsWithAnswer: [],
      showingQuestion: false,
      buttonState: [],
      answer: '',
      question: {
        current_question: 'what is the capital of Prince Edward Island ',
        answers: ["Charlottetown", "Georgetown", "Montague"]
      }
    };
  },
  sockets: {
    connect: function connect() {
      // Fired when the socket connects.
      this.isConnected = true;
      console.log('connected');
      this.joinChannel();
    },
    disconnect: function disconnect() {
      this.isConnected = false;
      console.log('disconnected');
    },
    // Fired when the server sends something on the "msg of questions room".
    msg: function msg(data) {
      console.log('receiving data from questions');
      this.question = data.body[0].question[0];
      this.showingQuestion = true;
      this.timeout = false;
      this.selectedAnswer = '';
      this.buttonState = [];
      this.startTimer(40);
      this.circleProgress = 0;
      this.buttonDisableState = false;
      this.showException = false;
      var number_of_records = data.body[0].number_of_records;

      if (this.question_number === number_of_records) {
        this.isLastQuestion = true;
      }

      console.log(this.question);
    }
  },
  mounted: function mounted() {
    var _this = this;

    console.log(this.$route.params.quizId);
    this.quizId = this.$route.params.quizId;
    localStorage.setItem('quizId', this.quizId);
    var timeToStartInterval = setInterval(function () {
      _this.timeToStart -= 1;

      if (_this.timeToStart <= 0) {
        _this.pullQuestion();

        clearInterval(timeToStartInterval);
      }
    }, 1000);
  },
  methods: {
    pullQuestion: function pullQuestion() {
      var _this2 = this;

      var obj = {
        'quiz_id': this.quizId,
        'page_number': this.question_number,
        'correct_answer': 1
      };
      commonController.getQuestion(obj).then(function (response) {
        console.log('pulling question', response.data.payload[0].question[0].options);
        _this2.optionsWithAnswer = response.data.payload[0].question[0].options;
        _this2.question_number += 1;
      }).catch(function (e) {
        console.log('error', e);

        _this2.errors.push(e);
      });
    },
    joinChannel: function joinChannel() {
      // Send the "pingServer" event to the server.
      console.log("joining questions");
      this.$socket.emit('join', 'questions');
    },
    showExceptionOnDOM: function showExceptionOnDOM() {
      return this.showException ? "exception" : "success";
    },
    startTimer: function startTimer(timeToExpire) {
      var _this3 = this;

      var timer = 0;
      var timerInterval = setInterval(function () {
        _this3.circleProgress = parseInt(timer / timeToExpire * 100);
        timer = timer + 1;

        if (_this3.circleProgress > 60) {
          _this3.showException = true;
        }

        if (_this3.circleProgress >= 101) {
          _this3.circleProgress = 100;
          _this3.timeout = true;
          clearInterval(timerInterval);

          _this3.showAnswerAndGetAnotherQuestion();
        }
      }, 200);
    },
    showAnswerAndGetAnotherQuestion: function showAnswerAndGetAnotherQuestion() {
      var _this4 = this;

      console.log('showing answer');
      this.optionsWithAnswer.forEach(function (option, index) {
        if (option.is_correct === 1) _this4.buttonState[index] = true;
      });
      setTimeout(function () {
        console.log('showing answer for 5 sec');

        if (_this4.isLastQuestion === true) {
          _this4.$router.push('/results');
        } else {
          _this4.pullQuestion();
        }
      }, 5000);
    }
  }
};
